# containing the digested version of static files. This
# manifest is generated by the `mix assets.deploy` task,
# which you should run after static files are built and
# before starting your production server.
# config :ldq, LdQWeb.Endpoint, cache_static_manifest: "priv/static/cache_manifest.json"
config :ldq, LdQWeb.Endpoint,
  url: [host: "www.atelier-icare.net", path: "/ldq", port: 8104],
  check_origin: ["https://www.atelier-icare.net/ldq"],
  http: [port: 8104],
  server: true, # ajout√© par Phil
  debug_errors: true #, #, # true pour voir les erreurs en production
  # cache_static_manifest: "priv/static/cache_manifest.json"

config :ldq, env: :prod # pour pouvoir faire Application.get_env(:ldq, :env) 

# Configures Swoosh API Client
config :swoosh, api_client: Swoosh.ApiClient.Finch, finch_name: LdQ.Finch

# Disable Swoosh Local Memory Storage
